    //--------------------- Wishbone Interface ---------------
    always @(posedge wb_clk or posedge wb_reset) begin
      if(wb_reset==1) begin
        wb_ack_o    <= #1 0;
        wb_dat_o    <= #1 0;
        control_reg <= #1 32'h0;
        data_reg    <= #1 127'h0;
        data_o      <= #1 127'h0;
      end
      else begin
        if(ready_i) begin
          control_reg[1] <= #1 1'b1;  
          data_reg <= #1 data_i;
        end
        else if(wb_stb_i && wb_cyc_i && wb_we_i && ~wb_ack_o) begin
          wb_ack_o<=#1 1;
         case(wb_adr_i[7:0])
             8'h0: begin
                   //Writing control register
                   control_reg<= #1 wb_dat_i;
                   end
             8'h4: begin
                   data_o[127:96]<= #1 wb_dat_i;
                   end                 
             8'h8: begin
                   data_o[95:64]<= #1 wb_dat_i;
                   end                 
			 8'hC: begin
                   data_o[63:32]<= #1 wb_dat_i;
                   end                 
             8'h10:begin
                   data_o[31:0]<= #1 wb_dat_i;
                   end                 
		 endcase
        end
        else if(wb_stb_i && wb_cyc_i && ~wb_we_i && ~wb_ack_o) begin
          wb_ack_o<=#1 1;
          case(wb_adr_i[7:0])
              8'h0: begin
                    wb_dat_o<= #1 control_reg;
                    control_reg[1]<=1'b0;
                    end
             8'h24: begin
                    wb_dat_o<= #1 data_reg[127:96];
                    end
             8'h28: begin
                    wb_dat_o<= #1 data_reg[95:64];
                    end
             8'h2C: begin
                    wb_dat_o<= #1 data_reg[63:32];
                    end
             8'h30: begin
                    wb_dat_o<= #1 data_reg[31:0];
                    end
          endcase
       end
       else begin
          wb_ack_o<=#1 0;
          control_reg[0]<= #1 1'b0;
       end
     end
    end

